import { Flex, Input, Image, Empty, message } from "antd";
import React, { useEffect, useState } from "react";
import FormularioBien from "./FormularioBien";
import "./styles/inventario.css";
const { Search } = Input;
const Inventario = ({ setTitle }) => {
  const [bienes, setBienes] = useState(null);
  const [buscar, setBuscar] = useState("");
  useEffect(() => {
    setTitle("Inventario");
  }, []);

  useEffect(() => {
    if (buscar !== "") {
      getBienes();
    } else {
      limpiarData();
    }
  }, [buscar]);

  const getBienes = async () => {
    if (buscar !== "") {
      const response = await fetch(
        `http://localhost:3006/api/v1/bienes/inventario?sbn=${buscar}`
      );

      if (response.ok) {
        const info = await response.json();
        setBienes(info); // Guardar los bienes en el estado si la respuesta es exitosa
      } else {
        // Si el estado es 403 o 404, mostrar un mensaje adecuado
        if (response.status === 403) {
          message.warning("El bien ya ha sido inventariado.");
        } else if (response.status === 404) {
          message.error("El bien no fue encontrado.");
        }
      }
    }
  };

  const limpiarData = async () => {
    setBienes(null);
  };

  const handleInputChange = (e) => {
    setBuscar(e.target.value); // Actualiza el estado de "buscar"
  };

  return (
    <>
      <Flex justify="flex-start" className="inventario-container">
        <Search
          placeholder="Buscar Bien"
          className="inventario-search"
          onChange={handleInputChange}
        />
      </Flex>

      <Flex justify="flex-start" className="inventario-content" gap={"10px"}>
        {bienes === null ? (
          <div
            style={{ width: "100%", height: "100%", backgroundColor: "white" }}
          >
            <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} />
          </div>
        ) : (
          <>
            <FormularioBien data={bienes} />
            <Flex className="inventario-image" justify="center" align="center">
              <Image
                width={"80%"}
                src={bienes?.foto}
                fallback="data:image/png;base64,"
              />
            </Flex>
          </>
        )}
      </Flex>
    </>
  );
};

export default Inventario;
